sourcesContent:
import jwt from 'jsonwebtoken';
import bcrypt from 'bcrypt';
import { ObjectID } from 'mongodb';
import { getDbConnection } from '../db';

export const updateUserPassword = {
    path: '/api/users/:userId/reset-password',
    method: 'put',
    handler: async (req, res) => {
        const { authorization } = req.headers;
        const { userId } = req.params;
        const { password } = req.body; // Make sure to extract the password from the body

        if (!authorization) {
            return res.status(401).json({ message: 'No authorization has been set' });
        }
        const token = authorization.split(' ')[1];

        jwt.verify(token, process.env.JWT_SECRET, async (err, decoded) => {
            if (err) return res.status(401).json({ message: 'Unable to verify token' });

            const { id } = decoded;

            if (id !== userId) return res.status(403).json({ message: 'Unable to update that user\'s data' });

            const db = getDbConnection('crm');

            // First, find the user
            const user = await db.collection('users').findOne({ _id: ObjectID(id) });
            if (!user) {
                return res.status(404).json({ message: 'User not found' });
            }

            // Hash the new password
            const hashedPassword = await bcrypt.hash(password, 10);

            // Proceed to update the user's password
            const result = await db.collection('users').findOneAndUpdate(
                { _id: ObjectID(id) },
                { $set: { password: hashedPassword } }, // Only update the password
                { returnOriginal: false }
            );

            if (!result.value) {
                return res.status(404).json({ message: 'User not found after update' });
            }

            // Optionally, you could return a success message or token
            return res.status(200).json({ message: 'Password updated successfully' });
        });
    }
};

